import React,{useState} from 'react'
import MenuBase from './MenuBase'
import "./MenuBase.css"
const AdminShajiDashCategoryCreate = () => {
    const [values, setvalues] = useState({
        createdCategory:"",
        category:"",
        error:""
    })
    const {createdCategory,category,error} = values;
    const successMessage = () => (
        <div
          className="alert alert-success mt-3"
          style={{ display: createdCategory ? "" : "none" }}
        >
          <h4>{createdCategory} created successfully</h4>
        </div>
      );
      const errorMessage = () => (
        <div
          className="alert alert-danger mt-3"
          style={{ display: error ? "" : "none" }}
        >
          <h4>{createdCategory} can't create category</h4>
        </div>
      );

      const onHandle = name => event => {
            setvalues({...values,[name]:event.target.value})
      }
    return (
        <MenuBase>
<div className="container">
    {successMessage()}
    {errorMessage()}
<form action="" className="">

<div class="form-group">
<label >Create Category</label>
<input onChange={onHandle("category")} type="text" class="form-control" id="exampleFormControlInput1" placeholder="Eg Indian,Chinese"/>
</div>
<button className="btn btn-info btn-block rounded cen">
Create
</button>
</form>
</div>
           
        </MenuBase>
    )
}



export default AdminShajiDashCategoryCreate;
