import React,{useState,useEffect} from 'react'
import { getallCategory } from './help/AddCategory'
import MenuBase from './MenuBase'


const AdminShajiDashMenuCreate = () => {

    const [values, setValues] = useState({
        name:"",
        formData:"",
        price:"",
        type:"",
        category:"",
        loading:false,
        error:"",
        categories:[],
        success:false,
        createdProduct:false
    })
    const handleChange = name => event => {
        setValues({...values,[name]:event.target.value})
    }
    const {name,formData,price,type,category,loading,error,success,createdProduct,categories} = values
    
    const successMessage = () => (
        <div
          className="alert alert-success mt-3"
          style={{ display: createdProduct ? "" : "none" }}
        >
          <h4>{createdProduct} created successfully</h4>
        </div>
      );

      const preloadCategories = () => {
        getallCategory().then(data => {
          if (data.error) {
            setValues({ ...values, error: data.error });
          } else {
            setValues({
              categories: data,

            });
          }
        });
      };

      useEffect(() => {
        preloadCategories()
      }, [])


    return (
            <MenuBase>
                <div className="container">
                <form>
                <div class="form-group">
                <label>Name</label>
                <input type="text" className="form-control" id="exampleFormControlInput1" placeholder="Food name"/>
                    </div>
                    <span>Food pic</span>
      <div className="form-group">
        <label className="btn btn-block btn-success">
          <input
            onChange={handleChange("photo")}
            type="file"
            name="photo"
            accept="image"
            placeholder="choose a file"
          />
        </label>
      </div>

      <div class="form-group">
    <label>Price in (INR)</label>
    <input type="number" class="form-control"/>
  </div>
  <div class="form-group">
    <label for="exampleFormControlSelect1">Type</label>
    <select class="form-control" id="exampleFormControlSelect1">
      <option>Veg</option>
      <option>Non-Veg</option>
      
    </select>
  </div>

  <div className="form-group">
        <select
          onChange={handleChange("category")}
          className="form-control"
          placeholder="Category"
        >
          <option>Select</option>
          {categories &&
            categories.map((cate, index) => (
              <option key={index} value={cate._id}>
                {cate.name}
              </option>
            ))}
        </select>
      </div>
  
                </form>
                </div>
                
            </MenuBase>
    )
}



export default AdminShajiDashMenuCreate;
