    }, [])

    const onLoadCart = () => {
        if(cartproducts){
            return(
                cartproducts.map((P,I)=>{
                    if(P!=null){
                        <div class="card-body">
            <div class="row">
              
                <div class="col-3  boxtxts"> {P.name} <br/>&#8377;{P.price}<br/>
                </div>
                <div className="col-4 countbtn"> 
                <ButtonGroup className="btnbox">
          <Button
            className="boxs"
            aria-label="reduce"
            onClick={
              
           ()=>{}
              

            }
          >
            <RemoveIcon fontSize="small" />
          </Button>
          {/* {showContBtn()} */}
          <Button
          className="boxs boxtxt"  
            aria-label="increase"
            onClick={
                // addToCart
                ()=>{}
         

            }
          >

          </Button>
        </ButtonGroup></div>
           
    </div>
        //    {cartSection()}
       </div>
                    }
                })

                )
        }
        
    }
    return (
        <div className="">
        <UserBase/>
        <div className="cart">
        <ul class="nav navbar-light backoffer justify-content-center ">
  <li class="nav-item ">
    <a class="nav-link text-white" href="#">Order</a>
  </li>
  <li class="nav-item">
    <a class="nav-link text-white" href="#">Offers</a>
  </li>
  
</ul>
<div className="card mt-5">

</div>
        </div>
        
        </div>
    )
}
