import React,{useEffect,useState} from 'react'
import Map from "mapmyindia-react";

import {loadOrderAndUserdata} from "./help/orderstatushelper"
export default function AdminShajiDashOrderOutDelivery({match}) {
    const [orders, setorders] = useState([])
    const [fleets, setfleets] = useState([])
    const [value,setValue] = useState({
        name:"",
        phone:"",
        address:"",
        location:[]
    })
    const {name,phone,address,location} = value;
    const onLoad = (orderId) => {
        loadOrderAndUserdata(orderId).then(res => {
            console.log(res)
            setorders(res.order)
            setfleets(res.fleets)
            setValue({...value,name:res.userdata.name,phone:res.userdata.phone,address:res.userdata.address,location:res.userdata.location.coordinates})
        })
        .catch(err => {
            console.log(err)
        })
    }
    useEffect(() => {
        onLoad(match.params.orderId)
    }, [])
    const showUserMap = () => {
        if(location){
            return(
                <Map
            markers={[
              {
                position: [10.9006464, 76.996596],
                draggable: true,
                title: "Shaji Dhaba",
                onClick: e => {
                  console.log("clicked ");
                },
                onDragend: e => {
                  console.log("dragged");
                }
              }
            ]}
          />
            )
        }
    }
    const userDatails = () => {
        if(name && phone && address){
            return (
                <div>
                    <div className="alert alert-danger" role="alert">
  <h6>{name}</h6>
            <p>{phone}</p>
            <div className="alert alert-light text-left" >
                    
            <p>
                <strong>Address : </strong>
                {address}
            </p>
            
</div>
</div>
                </div>
            )
        }
    }
    return (
        <div>
            <div className="mt-5 text-center">
            <h3>Delivery order</h3>
            <div className="container">
{userDatails()}
{showUserMap()}
            </div>
            </div>
        </div>
    )
}
