import React,{useEffect,useState} from 'react'
import Map from "mapmyindia-react";

import {loadOrderAndUserdata} from "./help/orderstatushelper"
export default function AdminShajiDashOrderOutDelivery({match}) {
    const [orders, setorders] = useState([])
    const [fleets, setfleets] = useState([])
    const [value,setValue] = useState({
        name:"",
        phone:"",
        address:"",
        location:[],
        latitude:"",
        longitude:""

    })
    const {name,phone,address,location,longitude,latitude} = value;
    const onLoad = (orderId) => {
        loadOrderAndUserdata(orderId).then(res => {
            console.log(res)
            console.log(res.fleets)
            setorders(res.order)
            setfleets(res.fleets)
            setValue({...value,name:res.userdata.name,phone:res.userdata.phone,address:res.userdata.address,location:res.userdata.location.coordinates,latitude:res.userdata.location.coordinates[0],longitude:res.userdata.location.coordinates[1]})
        })
        .catch(err => {
            console.log(err)
        })
    }
    useEffect(() => {
        onLoad(match.params.orderId)
    }, [])
    const showUserMap = () => {
        if(longitude && latitude){
            return(
                <Map
            markers={[
              {
                position: [latitude, longitude],

                draggable: true,
                title: "User Location",
                onClick: e => {
                  console.log("clicked ");
                },
                onDragend: e => {
                  console.log("dragged");
                }
              }
            ]}
        center={[latitude, longitude]}

          />
            )
        }
    }
    const chooseFleets = () => {
    //    Object.keys(fleets[0]).map((pro,i)=>{
    //     return <p>{i}</p>
    //    })
    fleets.map((pp,i)=>{
    return <p>hjouihjo</p>
    })
    // return <p>{JSON.stringify(fleets[0])}</p>
    }
    const userDatails = () => {
        if(name && phone && address){
            return (
                <div>
                    <div className="alert alert-danger" role="alert">
  <h6>{name}</h6>
            <p>{phone}</p>
            <div className="alert alert-light text-left">
            <p>
                <strong>Address : </strong>
                {address}
            

            </p>
            
</div>
</div>
                </div>
            )
        }
    }
    return (
        <div>
            <div className="mt-5 text-center">
            <h3>Delivery order</h3>
            <div className="container">
{userDatails()}
{Object.keys(fleets).map((F,I)=>{
                        return(
                            <div className="mb-4">
                                <div class="card">
      <h5 class="card-header">Name: {fleets[F].name}</h5>
      <div class="card-body">
        <h5 class="card-title">Phone: {fleets[F].phone}</h5>
        <h5 class="card-title">Password: {fleets[F].fid}</h5>

        <p class="card-text">Press edit if you have to edit the delivery account</p>
        <div className="row text-center" style={{backgroundColor:'#487EB0'}}>
            <div className="col-6">
            <a href={`/admin/shaji/dashboard/fleets/editfleets/${fleets[F]._id}`} class="btn btn-warning">Edit</a>

            </div>
            <div className="col-6">
        <a href="#" class="btn btn-danger">Delete</a>
                </div>
        </div>
      

      </div>
    </div>
                           
                                </div>
                         

                        )
                    })}
{showUserMap()}
            </div>
            </div>
        </div>
    )
}
