import React,{useState,useEffect} from 'react'
import UserBase from '../user/UserBase'
import { loadCart } from './helper/cartHelper';
import MenuCard from './MenuCard';

import ButtonGroup from '@material-ui/core/ButtonGroup';
import Button from '@material-ui/core/Button';
import AddIcon from '@material-ui/icons/Add';
import RemoveIcon from '@material-ui/icons/Remove';

export default function Purchase() {
    const [cartproducts, setcartproducts] = useState([]);
    const [reload, setReload] = useState(false);
    const [totalcount,setTotalcount] = useState(0);
    const [totalPrice,setTotalPrice] = useState(0); 
    const [thisProduct,setthisProduct] = useState(0); 

    useEffect(() => {
        setcartproducts(loadCart());
autoCountBtn()
        
      }, [reload]);
      const showPurchase = (name,price) =>{
        console.log(name);

         
      }

      const autoCountBtn = () => {
        let cart = [];
        let TCOUNT = 0;
        let TOTAL_PRICE = 0;
        let curentProduct = [];
        if (typeof window !== undefined) {
          if (localStorage.getItem("cart")) {
            cart = JSON.parse(localStorage.getItem("cart"));
          }
        
              cart.map((PRODUCT,INDEX)=>{
                if(PRODUCT != null){
                    curentProduct.push(PRODUCT.name)
                }
              })
              curentProduct = [...new Set(curentProduct)];
              console.log("curentProduct : ",curentProduct);
              curentProduct.map((cP,cI)=>{

                loadAllProducts(cP,cP.length)
              })
          }
        }
     
      const loadAllProducts = (product,qty) => {
        return (
          <div>
            {cartproducts.map((product, index) => {
              if(product!=null){
                
                return(
                    <div className="ml-4">
                        {/* {contQty(product)} */}
                        <div className="row">
                            <div className="col-4">
                            <h6>{product.name}</h6>

                            </div>
                            <div className="col-4">
                            <h6>{product.price}</h6>

                            </div>
                            <div className="col-4">
                            <h6>{thisProduct}</h6>

                            </div>
                           
                        </div>
                    </div>
                  )
              }
      })}
          </div>
        );
      };
    return (
        <div className="">
        <UserBase/>

        <div className="cart">
        <ul class="nav navbar-light backoffer justify-content-center ">
  <li class="nav-item ">
    <a class="nav-link text-white" href="#">Order</a>
  </li>
  <li class="nav-item">
    <a class="nav-link text-white" href="#">Offers</a>
  </li>
  
</ul>
<div className="card mt-5 cartpage">
{autoCountBtn()}
</div>
        </div>
        
        </div>
    )
}
