import React,{useState,useEffect} from 'react'
import { updateCategory } from '../../../../backend/controllers/category';
import MenuBase from './MenuBase'

export default function AdminShajiDashCategoryEdit({ match }) {
    const [values, setvalues] = useState({
        updatedCategory:"",
        name:"Category",
        error:"",
        success:false
    })
    const {updatedCategory,name,error,success} = values;
    const onHandle = name => event => {
        setvalues({...values,[name]:event.target.value})
  }
  useEffect(() => {
    onUpdate(match.params.cateId);
  }, []);
    const onUpdate = cateId => {
        // event.preventDefault();
        updateCategory(updateCategory).then(data => {
            if(data.error){
                setvalues({ ...values, error: data.error });

            }
            else{
                setvalues({...values,updatedCategory:data})
            }
        })
        
        };
        
    return (
            <MenuBase>
                <div className="container">
                <form action="" className="">

<div class="form-group">
    <label >Edit Category {name}</label>
<input onChange={onHandle("name")} type="text" name="name" class="form-control" id="exampleFormControlInput1" placeholder="Eg Indian,Chinese"/>
</div>
<button onClick={onUpdate} className="btn btn-info btn-block rounded cen">
Create
</button>
</form>
                </div>
                
            </MenuBase>
    )
}
