    }
    const onLoad = (userId) => {
        getUserDataFromOrder(userId).then(data => {
            if(data.error){
                console.log(data.error);
            }
            console.log(data.order);
            console.log(data.user);
            setuser(data.user.name);
            setuserphone(data.user.phone);
            setuserloc(data.user.location);
            setproductName.push(data.order.productname);
            setproductQty.push(data.order.productqty);
        })
    }
    useEffect(() => {
        onLoad(match.params.userId)
    }, [])
    return (
        <div>
            <h4>take order</h4>
    <h6>{user}</h6>
