import React,{useState,useEffect} from 'react'
import UserBase from '../user/UserBase'
import { loadCart } from './helper/cartHelper';
import MenuCard from './MenuCard';

import ButtonGroup from '@material-ui/core/ButtonGroup';
import Button from '@material-ui/core/Button';
import AddIcon from '@material-ui/icons/Add';
import RemoveIcon from '@material-ui/icons/Remove';

export default function Purchase() {
    const [cartproducts, setcartproducts] = useState([]);
    const [reload, setReload] = useState(false);
    const [totalcount,setTotalcount] = useState(0);
    const [totalPrice,setTotalPrice] = useState(0); 
    const [thisProduct,setthisProduct] = useState(0); 

    useEffect(() => {
        setcartproducts(loadCart());
autoCountBtn()
        
      }, [reload]);
      const showPurchase = (name,price) =>{
        console.log(name);

         
      }

      const autoCountBtn = () => {
        let cart = [];
        let TCOUNT = 0;
        let TOTAL_PRICE = 0;
      
        if (typeof window !== undefined) {
          if (localStorage.getItem("cart")) {
            cart = JSON.parse(localStorage.getItem("cart"));
          }
        
              cart.map((PRODUCT,INDEX)=>{
                if(PRODUCT != null){
                  TCOUNT = TCOUNT + 1;
                  TOTAL_PRICE =TOTAL_PRICE + PRODUCT  .price;
                }
              })
          setTotalcount(TCOUNT)
          setTotalPrice(TOTAL_PRICE);
      
          TCOUNT=0
          }
        }
        const contQty = (product) =>{
    let cart = [];
    let TOTOAL = 0;
  if (typeof window !== undefined) {
    if (localStorage.getItem("cart")) {
      cart = JSON.parse(localStorage.getItem("cart"));
    }
    cart.map((thisProduct,index)=>{
        if(thisProduct!=null){
            if(product.name === thisProduct.name){
                TOTOAL = TOTOAL +1
            }
        }
        
    })
    }
        }
      const loadAllProducts = () => {
        return (
          <div>
            {cartproducts.map((product, index) => {
              if(product!=null){
                return(
                    <div className="ml-4">
                        <div className="row">
                            <div className="col-4">
                            <h6>{product.name}</h6>

                            </div>
                            <div className="col-4">
                            <h6>{product.price}</h6>

                            </div>
                            <div className="col-4">
                          

                            </div>
                           
                        </div>
                    </div>
                  )
              }
      })}
          </div>
        );
      };
    return (
        <div className="">
        <UserBase/>

        <div className="cart">
        <ul class="nav navbar-light backoffer justify-content-center ">
  <li class="nav-item ">
    <a class="nav-link text-white" href="#">Order</a>
  </li>
  <li class="nav-item">
    <a class="nav-link text-white" href="#">Offers</a>
  </li>
  
</ul>
<div className="container">
<div className="card mt-5 cartpage">
{loadAllProducts()}
<div className="card">
<h6 classNAme="ml-5 totaltxtt">Total : &#8377;{totalPrice}</h6>
</div>
</div>
</div>

        </div>
        
        </div>
    )
}
