import React,{useState,useEffect} from 'react'
import { Link } from 'react-router-dom'
import AdminShajiDash from './AdminShajiDash'
import { getOrdersAdminAll } from './help/AddCategory'
import "./AdminShajiDashOrders.css"
export default function AdminShajiDashOrders() {
    const [error, seterror] = useState(false)
    const [orders, setorders] = useState("")
    const [price,setPrice] = useState([]);
    const [total,setTotal] = useState([]);
    const [status,setstatus] = useState([]);

    const [qty,setQty] = useState([]);
    const errorHandler = () => {
        if(error){
            return(
                <div className="alert alert-danger" role="alert">
                Sorry Admin some error happened !!!
              </div>
            )
          
        }
    }
    useEffect(() => {
        allOrdersToday()
    }, [])
    
    const allOrdersToday = () => {
        getOrdersAdminAll().then(res => {
            if(res.error){
                seterror(true)
                
            }
            console.log("Res",res.Pname);
            console.log("Res",res.Status);
            console.log("Res",res.Pprice);
            console.log("Res",res.Ptotal);

            
            setstatus(res.Status)
            console.log("Res",typeof(res.Pname));
            setPrice(res.Pprice)
            setOrders(res.Pname)
            setQty(res.Pqty)
            setQty(res.Pqty)
            setTotal(res.Ptotal)
        })
    }

    return (
        <AdminShajiDash>
            {errorHandler()}
            <div className="ordertop">
            <h6 className="mt-5">{JSON.stringify(orders)}</h6>
            {Object.keys(orders).map((key,index)=>{
                return(
                    <div key={index}>
                    <h6><strong>Item</strong> {orders[key]}</h6>
                <h6><strong>Status</strong> {status[index]}</h6>
                <h6>{price[index]}</h6>
                <h6>{qty[index]}</h6>
                <h5>TOTAL :{total[index]}</h5>

                    <hr/>
                
                </div>
                )
            })}
            </div>
        </AdminShajiDash>
    )
}
