import React, { useState } from "react";
import Base from "../core/Base";
import { Link } from "react-router-dom";
import { signup } from "../auth/helper";
import UserBase from "./UserBase";
import "./UserBase.css"
const Signup = () => {
  const [values, setValues] = useState({
    name: "",
    phone: "",
    error: "",
    success: false,
    loading:false
  });

  const { name, phone, error, success ,loading} = values;

  const handleChange = name => event => {
    setValues({ ...values, error: false, [name]: event.target.value });
  };

  const onSubmit = event => {
    event.preventDefault();
    setValues({ ...values, error: false });
    signup({ name, phone })
      .then(data => {
        if (data.error) {
          setValues({ ...values, error: data.error, success: false });
        } else {
          setValues({
            ...values,
            name: "",
            email: "",
            password: "",
            error: "",
            success: true
          });
        }
      })
      .catch(console.log("Error in signup"));
  };

  const signUpForm = () => {
    return (
      <div className="row">
        <div className="col-md-6 offset-sm-3 text-left">
          <form>
            <div className="form-group">
              <label className="text-light">Name</label>
              <input
                className="form-control"
                onChange={handleChange("name")}
                type="text"
                value={name}
              />
            </div>
            <div className="form-group">
              <label className="text-light">Email</label>
              <input
                className="form-control"
                onChange={handleChange("email")}
                type="phone"
                value={phone}
              />
            </div>


            <button onClick={onSubmit} className="btn btn-success btn-block">
              Submit
            </button>
          </form>
        </div>
      </div>
    );
  };

  const successMessage = () => {
    return (
      <div className="row">
        <div className="col-md-6 offset-sm-3 text-left">
          <div
            className="alert alert-success"
            style={{ display: success ? "" : "none" }}
          >
            New account was created successfully. Please
            <Link to="/signin">Login Here</Link>
          </div>
        </div>
      </div>
    );
  };

  const errorMessage = () => {
    return (
      <div className="row">
        <div className="col-md-6 offset-sm-3 text-left">
          <div
            className="alert alert-danger"
            style={{ display: error ? "" : "none" }}
          >
            {error}
          </div>
        </div>
      </div>
    );
  };

  return (
    <div className="userbase">
    <UserBase/>
      <div>
        <div className="container mt-5">
        <form>
  <div class="form-group">
    <label for="exampleInputEmail1">Name</label>
    <input type="text" class="form-control"/>
  </div>
  <div class="form-group">
    <label for="exampleInputPassword1">Phone</label>
    <input type="number" class="form-control"/>
  </div>
  <div class="form-group">
    <label for="exampleFormControlSelect1">Confirm Location</label>
    <select class="form-control" id="exampleFormControlSelect1">
      <option>Near Pollachi Main Road, Malumichampatty, Coimbatore, Tamil Nadu 641050</option>
      <option>Near Chettipalayam Rd, Guruvayur Nagar, Malumichampatti, Tamil Nadu 641050</option>
      <option>Near Chettipalayam Rd, Guruvayur Nagar, Malumichampatti, Tamil Nadu 641050</option>
      <option>No Near Malumichampatti</option>
    </select>
  </div>
  <div>

  <button type="submit" class="btn btn-warning btn-block rounded">Submit</button>
  <p className="alreadyuser"><small>If use are already a user, please sigin below</small> </p>
    <button className="btn btn-info signinbtn rounded">
      Signin
    </button>
  </div>
</form>
        </div>

      </div>

    </div>
  );
};

export default Signup;
