import React,{useState,useEffect} from 'react'
import UserBase from '../user/UserBase'
import { loadCart,loadCartBill ,calculateBill,findQty} from './helper/cartHelper';
import MenuCard from './MenuCard';

import ButtonGroup from '@material-ui/core/ButtonGroup';
import Button from '@material-ui/core/Button';
import AddIcon from '@material-ui/icons/Add';
import RemoveIcon from '@material-ui/icons/Remove';

export default function Purchase() {
    const [cartproducts, setcartproducts] = useState([]);
    const [reload, setReload] = useState(false);
    const [totalcount,setTotalcount] = useState(0);
    const [totalPrice,setTotalPrice] = useState(0); 
    const [finalProduct,setFinalProduct] = useState(0); 

    useEffect(() => {
        setcartproducts(loadCartBill());
        isfetchPriceQty()
        isproductQty()
      }, [reload]);
      console.log("cartproducts >> :",cartproducts);
      console.log("totalPrice :",totalPrice);
      console.log("finalProduct :",finalProduct);
      console.log("cartproducts :",cartproducts);
      
    //   fetchPriceQty(cartproducts)
    //   productQty(finalProduct)

      const isfetchPriceQty = () => {
          if(cartproducts){
            fetchPriceQty(cartproducts)
          }
      }
      const isproductQty = () =>{
          if(finalProduct){
            productQty(finalProduct)
          }
      }
      const fetchPriceQty = (cartproducts) => {
          if(cartproducts){
           
            setFinalProduct(calculateBill(cartproducts))
            
          }
      }

      const productQty = (finalProduct) => {
        if(finalProduct){
            console.log("totalPrice : ",finalProduct);
         
            setTotalPrice(findQty(finalProduct))
            console.log("totalPrice : ",totalPrice);
        }
    }
      
      
    //   const showBill = () => {
    //     if(finalProduct){
           
    //         finalProduct.map((P,I)=>{
    //                 return(

    //                     <div className="container">
    //                         <div className="row">
