const uuidv1 = require("uuid/v1");

export const addItemToCart = (item, next) => {
    let cart = [];
    let PRODUCTS = [];
    let total = 1;
    if (typeof window !== undefined) {
        if (localStorage.getItem("cart")) {
            cart = JSON.parse(localStorage.getItem("cart"));
        }
        cart.map((P, I) => {
            if (P != null) {
                if (P.name === item.name) {
                    total = total + 1
                }
            }
        })
        cart.push({
            ...item,
            count: total,
            // uid: uuidv1()
        });

        if (typeof window !== undefined) {
            if (localStorage.getItem(item.name)) {
                PRODUCTS = JSON.parse(localStorage.getItem(item.name));
            }
            PRODUCTS.map((P, I) => {
                if (P != null) {
                    if (P.name === item.name) {
                        total = total + 1
                    }
                }
            })
            PRODUCTS.push({
                ...item,
                count: total,
                // uid: uuidv1()
            });
            localStorage.setItem(item.name, JSON.stringify(PRODUCTS));
        }
        var lastname = localStorage.getItem("cart");
        window.localStorage.removeItem('cart');
        localStorage.setItem("cart", JSON.stringify(cart));


        next();
    }
};

export const loadCart = () => {
    if (typeof window !== undefined) {
        if (localStorage.getItem("cart")) {
            return JSON.parse(localStorage.getItem("cart"));
        }

    }
};

export const removeItemFromCart = product => {
    let cart = [];
    let removeProduct = [];
    let Products = [];
    console.log("Product uid : ", product.name);
    if (typeof window !== undefined) {
        if (localStorage.getItem("cart")) {
            cart = JSON.parse(localStorage.getItem("cart"));
            console.log("Cart : ", cart);
        }


        cart.map((p, i) => {
            if (p != null) {
                if (p.name === product.name) {
                    // cart.splice(i, 1);

                    removeProduct.push(p)
                }
            }

        });
        console.log("REMOVE PRODUCT : ", removeProduct);

        cart.map((p, i) => {
            if (p != null) {
                if (p.name === product.name) {
                    console.log(p.name);
                    delete cart[i];

                    // removeProduct.push(p)
                }
            }

        });
        // cart.append(p)
        console.log("Updated List : ", cart);
        removeProduct.pop();
        console.log("Updated RemoveProduct : ", removeProduct);
        removeProduct.map((P, I) => {
            if (P != null) {
                cart.push(P);
            }
        })
        if (typeof window !== undefined) {
            if (localStorage.getItem(product.name)) {
                Products = JSON.parse(localStorage.getItem(product.name));
                console.log("Products : ", Products);
            }
            Products.pop()
            localStorage.setItem("cart", JSON.stringify(Products));

        }
        console.log("Final cart   : ", cart);
        localStorage.setItem("cart", JSON.stringify(cart));
    }
    return cart;
};
