const uuidv1 = require("uuid/v1");

export const addItemToCart = (item, next) => {
    let cart = [];
    let total = 1;
    if (typeof window !== undefined) {
        if (localStorage.getItem("cart")) {
            cart = JSON.parse(localStorage.getItem("cart"));
        }
        cart.map((P, I) => {
            if (P != null) {
                if (P.name === item.name) {
                    total = total + 1
                }
            }
        })
        cart.push({
            ...item,
            count: total,
            // uid: uuidv1()
        });
        var lastname = localStorage.getItem("cart");
        window.localStorage.removeItem('cart');
        localStorage.setItem("cart", JSON.stringify(cart));
        next();
    }
};

export const loadCart = () => {
    if (typeof window !== undefined) {
        if (localStorage.getItem("cart")) {
            return JSON.parse(localStorage.getItem("cart"));
        }

    }
};

export const removeItemFromCart = product => {
    let cart = [];
    let removeProduct = [];
    console.log("Product uid : ", product);
    if (typeof window !== undefined) {
        if (localStorage.getItem("cart")) {
            cart = JSON.parse(localStorage.getItem("cart"));
        }


        cart.map((p, i) => {
            if (p != null) {
                if (p.name === product.name) {
                    // cart.splice(i, 1);

                    removeProduct.push(p)
                }
            }

        });
        console.log("REMOVE PRODUCT : ", removeProduct);

        cart.map((p, i) => {
            if (p != null) {
                if (p.name === product.name) {
                    console.log(p.name);
                    delete cart[i];

                    // removeProduct.push(p)
                }
            }

        });
        // cart.append(p)
        console.log("Updated List : ", cart);
        removeProduct.pop();
        console.log("Updated RemoveProduct : ", removeProduct);
        removeProduct.map((P, I) => {
            if (P != null) {
                cart.push(P);
            }
        })

        console.log("Final cart   : ", cart);
        localStorage.setItem("cart", JSON.stringify(cart));
    }
    return cart;
};
