import React,{useState,useEffect} from 'react'
import { createaProduct, getallCategory } from './help/AddCategory'
import MenuBase from './MenuBase'


const AdminShajiDashMenuCreate = () => {

    const [values, setValues] = useState({
        name:"",
        formData:"",
        price:"",
        type:"",
        category:"",
        loading:false,
        error:"",
        categories:[],
        success:false,
        createdProduct:false
    })

    const {
      name,
      description,
      price,
      stock,
      type,
      categories,
      category,
      loading,
      error,
      success,
      createdProduct,
      getaRedirect,
      formData
    } = values;
    const handleChange = name => event => {
      const value = name === "photo" ? event.target.files[0] : event.target.value;
      // formData.set(name, value);
      setValues({ ...values, [name]: value });
    };
    
    const successMessage = () => (
        <div
          className="alert alert-success mt-3"
          style={{ display: createdProduct ? "" : "none" }}
        >
          <h4>{createdProduct} created successfully</h4>
        </div>
      );

      const preloadCategories = () => {
        getallCategory().then(data => {
          if (data.error) {
            setValues({ ...values, error: data.error });
          } else {
            setValues({
              categories: data,

            });
          }
        });
      };

      useEffect(() => {
        preloadCategories()
      }, [])


      const onSubmit = event => {
        event.preventDefault();
        setValues({ ...values, error: "", loading: true });
        createaProduct( formData).then(data => {
          if (data.error) {
            setValues({ ...values, error: data.error });
          } else {
            setValues({
              ...values,
              name: "",
              description: "",
              price: "",
              photo: "",
              stock: "",
              loading: false,
              createdProduct: data.name
            });
          }
        });
      };



    return (
            <MenuBase>
                <div className="container">
                <form>
                <div class="form-group">
                <label>Name</label>
                <input type="text" onChange={handleChange("name")} className="form-control" id="exampleFormControlInput1" placeholder="Food name"/>
                    </div>
                    <span>Food pic</span>
      <div className="form-group">
        <label className="btn btn-block btn-success">
          <input
            onChange={handleChange("photo")}
            type="file"
            name="photo"
            accept="image"
            placeholder="choose a file"
          />
        </label>
      </div>

      <div class="form-group">
    <label>Price in (INR)</label>
    <input onChange={handleChange("price")} placeholder="Enter price per Item"  type="number" class="form-control"/>
  </div>
  <div class="form-group">
    <label for="exampleFormControlSelect1">Type</label>
    <select onChange={handleChange("type")} className="form-control" id="exampleFormControlSelect1">
      <option>Veg</option>
      <option>Non-Veg</option>
      
    </select>
  </div>

  <div className="form-group">
        <select
          onChange={handleChange("category")}
          className="form-control"
          placeholder="Category"
        >
          <option>Category</option>
          {categories &&
            categories.map((cate, index) => (
              <option key={index} value={cate._id}>
                {cate.name}
              </option>
            ))}
        </select>
      </div>
      <br/>
      <button onSubmit={onSubmit} className="btn btn-warning btn-block rounded">
        Create Menu 
      </button>
  
                </form>
                </div>
                
            </MenuBase>
    )
}



export default AdminShajiDashMenuCreate;
