export const addItemToCart = (item, next) => {
    let cart = [];
    let total = 1;
    if (typeof window !== undefined) {
        if (localStorage.getItem("cart")) {
            cart = JSON.parse(localStorage.getItem("cart"));
        }
        cart.map((P, I) => {
            if (P != null) {
                if (P.name === item.name) {
                    total = total + 1
                }
