import React from 'react'
import UserBase from '../user/UserBase'
import "./Bill.css"
import { loadCartBill,contQty } from './helper/cartHelper'

export default function Bill() {
    const showProductQty = () => {
        const PRODUCTS  = loadCartBill();
        console.log("--->>>",PRODUCTS);
        const y = contQty(PRODUCTS)
        console.log("--->>> y ",y);

          return(
              <div>
                    {PRODUCTS.map((pro,I)=>{
                return(
                    <div className="container card">
                        <div className="row">
                            <div className="col-4">
                                <p className="">{pro}</p>
                            </div>
                            <div className="col-4">
                            <p className="">{y[I]}</p>

                            </div>
                        </div>
                    </div>
                )
            })

              }
              </div>
              
          )
       
        
      }
    return (
        <div>
            <UserBase/>
            <div className="container mt-4">
                {showProductQty()}
            </div>
        </div>
    )
}
