import React,{useState,useEffect} from 'react'
import "./Bill.css"
import { loadCart,loadCartBill,findQty } from './helper/cartHelper';

export default function Cod() {
  const [cartproducts, setcartproducts] = useState([]);
  const [reload, setReload] = useState(false);
  const [totalcount,setTotalcount] = useState(0);
  const [totalPrice,setTotalPrice] = useState(0); 
  const [thisProduct,setthisProduct] = useState(0); 
  const [thisNameProducts,setthisNameProducts] = useState(0); 

  useEffect(() => {
    setcartproducts(loadCart());
    setthisProduct(loadCartBill())
    setthisNameProducts(loadCartBill());

    
  }, [reload]);
    const [location, setlocation] = useState(0)
    const locationUser = event => {
        setlocation(event.target.value) //Location done
    }
    const confirmOrder = (event) => {
        event.preventDefault();
        const PRODUCTS  = loadCartBill();
        console.log("S o ",PRODUCTS);
        const y = contQty(PRODUCTS)
        console.log("S o  y ",y);
        console.log("location : ",location);
        console.log("cartproducts : S o",cartproducts);

    }
  
    const contQty = (product) =>{
      let cart = [];
      let yy = [];
      console.log("--->>> y product ",product);
  
      let TOTOAL = 0;
    if (typeof window !== undefined) {
      if (localStorage.getItem("cart")) {
        cart = JSON.parse(localStorage.getItem("cart"));
        product.map((pro,I)=>{
          cart.map((cpor,cI)=>{
              if(cpor!=null &&  pro === cpor.name){
                  TOTOAL = TOTOAL + 1;
              }
          })
          yy.push(TOTOAL)
          TOTOAL=0;
      })
      }
   
    }
   return yy;
          }


    return (
        <div>
            //1. get exact address
            //2. and phone number to call
            //3. 
            <h1>COD</h1>
            <div className="container codbox">
            <div class="alert alert-primary" role="alert">
  We are currently available in<a href="#" class="alert-link"> Malumichampatti</a>. And we can do delivery !!
</div>
            <form>
  <div class="form-group">
    <label>Your Address</label>
    <input placeholder="Enter the address" onChange={(e)=>{locationUser(e)}} type="text" class="form-control" />
  </div>
  

  <button onClick={confirmOrder} type="submit" class="btn btn-primary">Confirm Address</button>
</form>

            </div>
        </div>
    )
}
