import React,{useState} from 'react'
import { makeStyles } from '@material-ui/core/styles';
import Badge from '@material-ui/core/Badge';
import "./MenuCard.css"
import CartCardBtn from "./CartCardBtn";

import ButtonGroup from '@material-ui/core/ButtonGroup';
import Button from '@material-ui/core/Button';
import AddIcon from '@material-ui/icons/Add';
import RemoveIcon from '@material-ui/icons/Remove';
import clsx from 'clsx';
import Chip from '@material-ui/core/Chip';
import Avatar from '@material-ui/core/Avatar';
import DoneIcon from '@material-ui/icons/Done';
import ImageHelper from './helper/ImageHelper';
const useStyles = makeStyles((theme) => ({
    root: {
      display: 'flex',
      flexDirection: 'column',
      '& > *': {
        marginBottom: theme.spacing(2),
      },
      '& .MuiBadge-root': {
        marginRight: theme.spacing(4),
      },
    },
  }));

const MenuCard = ({
    product,
    addtoCart = true,
    removeFromCart = false,
}) => {
    const [count, setCount] = React.useState(0);
    const [invisible, setInvisible] = React.useState(false);
    const [total,setTotal] = React.useState(0);
    const [plus,setPlus] = React.useState(0);
    const [minus,setMinus] = React.useState(0);

    const handleBadgeVisibility = () => {
      setInvisible(!invisible);
    };
    
//   const [count, setCount] = useState(product.count);    
const useStyles = makeStyles((theme) => ({
    root: {
      flexGrow: 1,
    },
    paper: {
      padding: theme.spacing(2),
      margin: 'auto',
      maxWidth: 500,
    },
    image: {
      width: 128,
      height: 128,
    },
    img: {
      margin: 'auto',
      display: 'block',
      maxWidth: '100%',
      maxHeight: '100%',
    },
  }));
  const cartTitle = product ? product.name : "A photo from pexels";
  const cartDescrption = product ? product.description : "Default description";
  const cartPrice = product ? product.price : "DEFAULT";
  const cartType = product ? product.type : "DEFAULT";
  const classes = useStyles();

const showContBtn = () => {
  if(count>0){
    return (
      <Button className="boxs">
      <span className="itemcen pt-1 boxtxt"><h6>{count}</h6></span>
      </Button>
    );
  }
  
}
const circle = <div className={clsx(classes.shape, classes.shapeCircle)} />;

const cartSection = () => {
  if(count>0){
    return (
      <div className="card cartcard fixed-bottom">
        <Badge  overlap="circle" badgeContent=" " variant="dot">
        {circle}

        <CartCardBtn total={total}/>  
      </Badge>
            
        </div>
      
        );
  }
 
}
    return (
       <div className="">
           
      
        <div class="card-body">
            <div class="row">
                <div class="col-4"><div className="imgbotbox">
                  <ImageHelper product={product}/>
                  </div></div>
                <div class="col-3  boxtxts"> {cartTitle} <br/>&#8377;{cartPrice}<br/>
                <div className="typeboxs">
                  <h6 className="typetxt">Veg</h6>
                </div>
                </div>
                <div className="col-4 countbtn"> 
                <ButtonGroup className="btnbox">
          <Button
            className="boxs"
            aria-label="reduce"
            onClick={() => {
              setCount(Math.max(count - 1, 0));
              setMinus(count+1)
              console.log("Minus : ",minus)

              setTotal(total-minus)
            }}
          >
            <RemoveIcon fontSize="small" />
          </Button>
          {showContBtn()}
          <Button
          className="boxs boxtxt"  
            aria-label="increase"
            onClick={() => {
              setCount(count + 1);
              setPlus(count)
              console.log("Plus : ",plus)
              setTotal(total+plus)
            }}
          >
            <AddIcon fontSize="small" />
          </Button>
        </ButtonGroup></div>
            </div>
    </div>
           {cartSection()}
       </div>
            
                

    )
}
export default MenuCard
