import React,{useState} from 'react'
import { makeStyles } from '@material-ui/core/styles';
import Badge from '@material-ui/core/Badge';
import "./MenuCard.css"
import ButtonGroup from '@material-ui/core/ButtonGroup';
import Button from '@material-ui/core/Button';
import AddIcon from '@material-ui/icons/Add';
import RemoveIcon from '@material-ui/icons/Remove';
import MailIcon from '@material-ui/icons/Mail';
import Switch from '@material-ui/core/Switch';
import FormControlLabel from '@material-ui/core/FormControlLabel';
const useStyles = makeStyles((theme) => ({
    root: {
      display: 'flex',
      flexDirection: 'column',
      '& > *': {
        marginBottom: theme.spacing(2),
      },
      '& .MuiBadge-root': {
        marginRight: theme.spacing(4),
      },
    },
  }));

const MenuCard = ({
    product,
    addtoCart = true,
    removeFromCart = false,
}) => {
    const [count, setCount] = React.useState(1);
    const [invisible, setInvisible] = React.useState(false);
  
    const handleBadgeVisibility = () => {
      setInvisible(!invisible);
    };
  
//   const [count, setCount] = useState(product.count);    
const useStyles = makeStyles((theme) => ({
    root: {
      flexGrow: 1,
    },
    paper: {
      padding: theme.spacing(2),
      margin: 'auto',
      maxWidth: 500,
    },
    image: {
      width: 128,
      height: 128,
    },
    img: {
      margin: 'auto',
      display: 'block',
      maxWidth: '100%',
      maxHeight: '100%',
    },
  }));
  const cartTitle = product ? product.name : "A photo from pexels";
  const cartDescrption = product ? product.description : "Default description";
  const cartPrice = product ? product.price : "DEFAULT";

    return (
       <div className="">
           <div class="card">
        <div class="card-body">
            <div class="row">
                <div class="col-3">img</div>
                <div class="col-4">{cartTitle} <br/>&#8377;{cartPrice}</div>
                <div className="col-3"> <ButtonGroup className="btnbox">
          <Button
            className="boxs"
            aria-label="reduce"
            onClick={() => {
              setCount(Math.max(count - 1, 0));
            }}
          >
            <RemoveIcon fontSize="small" />
          </Button>
          <Button className="boxs">
          <span className="itemcen pt-1 boxtxt"> <h6 className="boxtxt" >{count}</h6></span>
          </Button>
          <Button
          className="boxs boxtxt"  
            aria-label="increase"
            onClick={() => {
              setCount(count + 1);
            }}
          >
            <AddIcon fontSize="small" />
          </Button>
        </ButtonGroup></div>
            </div>
        </div>
    </div>
       
       </div>
            
                

    )
}
export default MenuCard
