import React,{useState,useEffect} from 'react'
import { isAutheticated } from '../auth/helper'
import { getallUserOrder } from './helper/orderHelper'
import MenuBar from './MenuBar'

export default function Order() {
    const [userUId, setuserUId] = useState(0)
    const [orders,setOrders] = useState([]);
    const [name,setName] = useState([]);
    const [price,setPrice] = useState([]);
    const [qty,setQty] = useState([]);
    
    const loadUserOrders = userID =>{
        console.log(userID);
        getallUserOrder(userID,token).then((res)=>{
            console.log("Res",res.orders);
            setOrders(res.orders)
        })
        .catch(err=>{
            console.log(err);
        })
    }
 
    const userId = isAutheticated().user._id;
    const token = isAutheticated().token;
    useEffect(() => {
        loadUserOrders(userId)
        setName(displayOrders())
    }, [])

    const displayOrders = () =>{
        const productname = [];
        const productprice = [];
        const productqty = [];
        // const productname = [];

         if(orders){
      

             const O = orders
             console.log(">>",O);
             if(O[0] !== undefined){
                console.log(">>",O[1].productname);
                const kk = JSON.stringify(O[1]);
               
                O.map((x,i)=>{
                    productname.push(x.productname)
                    productprice.push(x.productprice)
                    productqty.push(x.productqty);
                    

                })
                console.log(productname);
                return(productname)
             }
         }
    }
    return (
        <div>
            <h1>Orders</h1>
            <div className="container">
            {displayOrders()}

            </div>
            
            <MenuBar/>
        </div>
    )
}
